// Transform WooCommerce product to JTL FFN CreateProductRequest format
const product = $input.first().json;

// Helper function to safely get nested values
function safeGet(obj, path, defaultValue) {
  if (!obj) return defaultValue;
  const keys = path.split('.');
  let result = obj;
  for (const key of keys) {
    if (result && result[key] !== undefined) {
      result = result[key];
    } else {
      return defaultValue;
    }
  }
  return result !== undefined ? result : defaultValue;
}

// Build identifier object (at least one identifier required)
const identifier = {
  ean: safeGet(product, 'sku', null), // Using SKU as EAN for now
};

// Build dimensions (convert from cm to meters for JTL FFN)
const dimensions = product.dimensions || {};
const length = dimensions.length ? parseFloat(dimensions.length) / 100 : 0;
const width = dimensions.width ? parseFloat(dimensions.width) / 100 : 0;
const height = dimensions.height ? parseFloat(dimensions.height) / 100 : 0;

// Build attributes array (key/value pairs)
const attributes = [];

// Add platform info
attributes.push({
  key: 'platform',
  value: 'woocommerce'
});

// Add external product ID
attributes.push({
  key: 'externalProductId',
  value: product.id ? product.id.toString() : ''
});

// Add product type
if (product.type) {
  attributes.push({
    key: 'productType',
    value: product.type
  });
}

// Add categories
if (product.categories && product.categories.length > 0) {
  const categoryNames = product.categories.map(function(cat) {
    return cat.name;
  }).join(', ');
  attributes.push({
    key: 'categories',
    value: categoryNames
  });
}

// Build the JTL FFN product request
// Note: Weight must be > 0, so use 0.1 kg as default if not specified
const weight = product.weight && parseFloat(product.weight) > 0 ? parseFloat(product.weight) : 0.1;

const jtlProduct = {
  name: product.name || 'Unnamed Product',
  merchantSku: product.sku || product.id.toString(),
  identifier: identifier,
  description: product.description || null,
  attributes: attributes,
  weight: weight,
  length: length || 0.01,
  width: width || 0.01,
  height: height || 0.01
};

return [{ json: jtlProduct }];
