// Filter to only the product with the SKU we want
const allItems = $input.all();
const targetSku = 'Testing-121212'; // Change this to the SKU you want to sync

for (const item of allItems) {
  const product = item.json;
  const variants = product.variants || [];

  // Check if any variant has the target SKU
  for (const variant of variants) {
    if (variant.sku === targetSku) {
      return [item]; // Return this product
    }
  }
}

// If no match found, return error
throw new Error(`Product with SKU '${targetSku}' not found in Shopify`);

// Transform Shopify product to JTL FFN CreateProductRequest format
const product = $input.first().json;

// Get the first variant for main product data
const firstVariant = product.variants && product.variants[0] ? product.variants[0] : {};

// Build identifier object (at least one identifier required)
const identifier = {
  ean: firstVariant.barcode || firstVariant.sku || null
};

// Build attributes array
const attributes = [];

attributes.push({
  key: 'platform',
  value: 'shopify'
});

attributes.push({
  key: 'externalProductId',
  value: product.id ? product.id.toString() : ''
});

if (product.product_type) {
  attributes.push({
    key: 'productType',
    value: product.product_type
  });
}

if (product.vendor) {
  attributes.push({
    key: 'vendor',
    value: product.vendor
  });
}

if (product.tags) {
  attributes.push({
    key: 'tags',
    value: product.tags
  });
}

// Get weight (convert from grams to kg if needed)
let weight = firstVariant.weight || 0;
if (firstVariant.weight_unit === 'g' || firstVariant.weight_unit === 'grams') {
  weight = weight / 1000;
} else if (!weight || weight === 0) {
  weight = 0.1; // Default weight
}

// Build the JTL FFN product request
const jtlProduct = {
  name: product.title || 'Unnamed Product',
  merchantSku: firstVariant.sku || product.id.toString(),
  identifier: identifier,
  description: product.body_html || null,
  attributes: attributes,
  weight: weight,
  length: 0.01,
  width: 0.01,
  height: 0.01
};

return [{ json: jtlProduct }];
